      return this.$i18n.locales.filter((i) => i.code !== this.$i18n.locale)
    },
  },
  mounted() {
    // eslint-disable-next-line dot-notation
    // this.$refs['home'].focus()
  },
  methods: {
    toggleLocale() {
      this.$i18n.locale === 'ar'
        ? (this.$i18n.locale = 'en')
        : (this.$i18n.locale = 'ar')
      this.$i18n.setLocaleCookie(this.$i18n.locale)
