<style>
.overlay {
  height: 100%;
  width: 0;
  position: fixed;
  background-color: rgb(255, 255, 255);
  background-color: rgba(0, 96, 128, 0.9);
  overflow-x: hidden;
  transition: 0.5s;
}

.overlay-content {
  position: relative;
  top: 25%;
  width: 100%;
  text-align: center;
  margin-top: 30px;
}
.fade-enter-active,
.fade-leave-active {
  transition: opacity 0.5s;
}
.fade-enter, .fade-leave-to /* .fade-leave-active below version 2.1.8 */ {
